package com.etoitau.leetcode.countandsay;

import java.util.ArrayList;
import java.util.List;

/**
 * LeetCode Problem 38 - Count and Say
 *
 * Result:
 * Runtime: 0 ms, faster than 100.00%
 * Memory Usage: 34.1 MB, less than 100.00%
 *
 * Description:
 * The count-and-say sequence is the sequence of integers with the first five terms as following:
 * 1.     1
 * 2.     11
 * 3.     21
 * 4.     1211
 * 5.     111221
 * 1 is read off as "one 1" or 11.
 * 11 is read off as "two 1s" or 21.
 * 21 is read off as "one 2, then one 1" or 1211.
 *
 * Given an integer n where 1 ≤ n ≤ 30, generate the nth term of the count-and-say sequence.
 *
 * Note: Each term of the sequence of integers will be represented as a string.
 */


/**
 * Object for generating the Count and Say Series
 */
class CountSaySeries {
    // the portion of the sequence that the object knows is stored here
    private List<String> sequence = new ArrayList<>();
    {
        sequence.add("1");
    }

    CountSaySeries() {}

    CountSaySeries(int startingSize) {
        // if a starting size is given, calculate and store the first startingSize elements of the sequence
        if (startingSize > 0)
            generate(startingSize);
    }

    public String get(int nth) {
        // get the nth element in the count and say series
        // note:
        //  this is counting from 1 (the first element)
        //  new elements are only generated if and when needed (lazy)

        if (nth < 1) { return null; }

        if (nth - 1 < sequence.size()) { return sequence.get(nth - 1); }

        generate(nth);

        return sequence.get(nth - 1);
    }

    private void generate(int nth) {
        if (nth <= sequence.size()) { return; }

        // start from largest available so far
        String str = sequence.get(sequence.size() - 1);

        StringBuilder sb = new StringBuilder();

        // holds current character being counted
        char val;

        while(sequence.size() < nth) {
            // new entry in sequence, reset StringBuilder
            sb.setLength(0);

            // generate next entry by reading previous
            // j is current position in previous
            int j = 0;
            while (j < str.length()) {
                // go through previous entry counting how many of each char
                // note it is known that the count and say series will only have characters 1, 2, and 3
                //  (there are never more than 3 of the same character in a row)

                // get current character
                val = str.charAt(j);

                // count and say
                if (j + 1 < str.length() && str.charAt(j + 1) == val) {
                    // if next char is a match
                    if (j + 2 < str.length() && str.charAt(j + 2) == val) {
                        // and character after that
                        sb.append('3').append(val);
                        j += 3;
                    } else {
                        sb.append('2').append(val);
                        j += 2;
                    }
                } else {
                    sb.append('1').append(val);
                    j++;
                }
            }
            // set current as previous for next loop
            str = sb.toString();
            // add to library
            sequence.add(str);
        }
    }

    @Override
    public String toString() {
        // enumerates all entries known to the object
        // 1. 1
        // 2. 11
        // 3. 21 ...
        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < sequence.size(); i++) {
            sb.append(i + 1).append(". ").append(sequence.get(i)).append(System.lineSeparator());
        }
        sb.setLength(sb.length() - System.lineSeparator().length());
        return sb.toString();
    }

    public String toCode() {
        // prints a string array variable declaration with all entries known to the object
        String tab = "    ";
        StringBuilder sb = new StringBuilder();
        sb.append("String[] sequence = new String[] {").append(System.lineSeparator());
        for (String entry: sequence) {
            sb.append(tab).append("\"").append(entry).append("\",").append(System.lineSeparator());
        }
        sb.setLength(sb.length() - 1 - System.lineSeparator().length());
        sb.append(System.lineSeparator()).append("};");
        return sb.toString();
    }
}

// get the array of entries for use in the solution
public class Main {
    public static void main(String[] args) {
        CountSaySeries css = new CountSaySeries(30);
        System.out.println(css.toCode());
    }
}

